import React from "react";

function TimerComponent() {
  const [time, setTime] = React.useState(5);
  console.log("업데이트");
  function update() {
    setTime(time * 2);
  }
  return (
    <div>
      <h3>{time}초</h3>
      <button onClick={update}>1씩 올려주세요</button>
    </div>
  );
}

export default TimerComponent;
