import { useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import "./index.css";

function SelfiePage() {
  const { id } = useParams();
  const [selfie, setSelfie] = useState(null);

  useEffect(function () {
    axios
      .get(`http://localhost:8080/products/${id}`)
      .then(function (result) {
        setSelfie(result.data.product);
        console.log(result);
      })
      .catch(function (error) {
        console.log("오류 발생: " + error);
      });
  }, []);

  if (selfie === null) {
    return <h1>사진 정보를 받고 있습니다...</h1>;
  }

  return (
    <div>
      <div id="image-box">
        <img src={"/" + selfie.imageUrl}></img>
      </div>
      <div id="profile-box">
        <span id="short">{selfie.short}</span>
      </div>
      <div id="contents-box">
        <div id="date">{selfie.date}</div>
        <div id="createAt">작성 날짜: 2022.02.12 01:25</div>
        <div id="description">{selfie.description}</div>
        <div id="icon">
          <img src="/images/icons/avatar.png"></img>
          <span>{selfie.place}</span>
        </div>
      </div>
    </div>
  );
}

export default SelfiePage;
