import "./index.css";
import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";
import dayjs from "dayjs";
import localizedFormat from "dayjs/plugin/localizedFormat";

dayjs.extend(localizedFormat);

function MainPage() {
  const [products, setProducts] = React.useState([]);

  React.useEffect(function () {
    axios
      .get("http://localhost:8080/products")
      .then(function (result) {
        const products = result.data.products;
        setProducts(products);
      })
      .catch(function (error) {
        console.log("에러발생: ", error);
      });
  }, []);

  return (
    <div>
      <div id="banner">
        <img src="images/banners/banner1a.PNG" alt="bannerPic" />
      </div>
      <h1 id="list-title">하연이의 사진들</h1>
      <div id="selfie-list">
        {products.map(function (product, index) {
          return (
            <div className="selfie-card" key={index}>
              <Link className="selfie-link" to={`selfies/${product.id}`}>
                <div className="img-cover">
                  <img className="selfie-img" src={product.imageUrl}></img>
                </div>
                <div className="selfie-contents">
                  <span className="selfie-date">
                    {dayjs(product.createdAt).format("YY.MM.DD LT")}
                  </span>
                  <span className="selfie-short">{product.short}</span>
                  <div className="product-footer">
                    <div className="selfie-place">
                      <img
                        className="selfie-avatar"
                        src="images/icons/avatar.png"
                      ></img>
                      <span>{product.place}</span>
                    </div>
                    <span className="selfie-weather">
                      날씨 : {product.weather}
                    </span>
                  </div>
                </div>
              </Link>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default MainPage;
